queries.compiledQueryString:
select GTFS.stops.stop_id, stop_lat, stop_lon, stop_name, arrival_time, stop_sequence
from GTFS.stops JOIN GTFS.stop_times ON (GTFS.stop_times.stop_id = GTFS.stops.stop_id and GTFS.stop_times.trip_id in (select trip_id from OCTData.feedmessage natural join GTFS.trips where run_id = '${inputs.selected_timestamp.value}' and trip_route = '${inputs.selected_route.value}'))
order by stop_sequence

queries.inputQueryString:
select GTFS.stops.stop_id, stop_lat, stop_lon, stop_name, arrival_time, stop_sequence
from GTFS.stops JOIN GTFS.stop_times ON (GTFS.stop_times.stop_id = GTFS.stops.stop_id and GTFS.stop_times.trip_id in ${trips})
order by stop_sequence
